mapper.select:

SELECT  OFFER_ID
FROM  OFFER
WHERE OFFER_ID=#{offerId,jdbcType=BIGINT}


SELECT  OFFER_OBJ_REL_ID, OFFER_ID, OBJ_TYPE, OBJ_ID, ROLE_ID, EFF_DATE, EXP_DATE, APPLY_REGION_ID, 
  STATUS_CD, CREATE_STAFF, UPDATE_STAFF, CREATE_DATE, STATUS_DATE, UPDATE_DATE, REMARK
FROM  OFFER_OBJ_REL
WHERE OFFER_ID=#{offerId,jdbcType=BIGINT}
 and obj_id=#{prodId,jdbcType=BIGINT}

SELECT  offer_inst_Attr_id offerInstAttrId,route_id
FROM OFFER_inst_attr
WHERE OFFER_inst_attr_id = #{offerInstAttrId,jdbcType=BIGINT}
and route_id = #{routeId,jdbcType=BIGINT}

SELECT  offer_inst_Attr_id offerInstAttrId,route_id routeId
FROM OFFER_inst_attr
WHERE OFFER_INST_ID = #{offerInstId,jdbcType=BIGINT}
and route_id = #{routeId,jdbcType=BIGINT}
AND EXP_DATE > #{expDate,jdbcType=VARCHAR}

SELECT  OFFER_INST_ATTR_ID offerInstAttrId, OFFER_INST_ID offerInstId, PAR_OFFER_INST_ATTR_ID parOfferInstAttrId, OBJ_TYPE objType,
 OBJ_ID objId, ATTR_ID attrId, ATTR_VALUE_ID attrValueId, ATTR_VALUE attrValue, EFF_DATE effDate, EXP_DATE expDate, 
 APPLY_REGION_ID applyRegionId, STATUS_CD statusCd, CREATE_STAFF createStaff, UPDATE_STAFF updateStaff, CREATE_DATE createDate, 
 STATUS_DATE statusDate, UPDATE_DATE updateDate, BUSI_MOD_EFF_DATE busiModEffDate, LAST_ORDER_ITEM_ID lastOrderItemId, REMARK remark, 
 ROUTE_ID routeId
FROM OFFER_inst_attr
WHERE ATTR_ID = #{attrId,jdbcType=BIGINT}
      AND OFFER_INST_ID = #{offerInstId,jdbcType=BIGINT}
      AND EXP_DATE > #{expDate,jdbcType=DATE}
      and route_id = #{routeId,jdbcType=BIGINT}

SELECT  OFFER_INST_ID offerInstId, OFFER_ID offerId, OFFER_TYPE offerType, OWNER_CUST_ID ownerCustId, EFF_DATE effDate, EXP_DATE expDate, OFFER_AGREE_ID offerAgreeId,
    CREATE_ORG_ID createOrgId, EXP_PROC_METHOD expProcMethod, LAN_ID lanId, REGION_ID regionId, STATUS_CD statusCd, CREATE_STAFF createStaff, UPDATE_STAFF updateStaff,
    CREATE_DATE createDate, STATUS_DATE statusDate, UPDATE_DATE updateDate, BUSI_MOD_DATE busiModDate, LAST_ORDER_ITEM_ID lastOrderItemId, REMARK remark,
    EXT_OFFER_INST_ID extOfferInstId, ROUTE_ID routeId
FROM OFFER_inst
WHERE OFFER_inst_id = #{offerInstId,jdbcType=BIGINT}
and route_id = #{routeId,jdbcType=BIGINT}
and exp_date > #{expDate,jdbcType=VARCHAR}

SELECT  OFFER_OBJ_INST_REL_ID offerObjInstRelId, OFFER_INST_ID offerInstId, OBJ_TYPE objType, OBJ_ID objId, OFFER_OBJ_REL_ID offerObjRelId, ROLE_ID roleId,
    EFF_DATE effDate, EXP_DATE expDate, STATUS_CD statusCd, CREATE_STAFF createStaff, UPDATE_STAFF updateStaff, CREATE_DATE createDate, STATUS_DATE statusDate,
    UPDATE_DATE updateDate, LAST_ORDER_ITEM_ID lastOrderItemId, REMARK remark, ROUTE_ID routeId
FROM OFFER_OBJ_inst_REL
WHERE OFFER_OBJ_inst_REL_ID = #{offerObjInstRelId,jdbcType=BIGINT}
AND OFFER_INST_ID = #{offerInstId,jdbcType=BIGINT}
and route_id = #{routeId,jdbcType=BIGINT}

SELECT  OFFER_OBJ_INST_REL_ID offerObjInstRelId, OFFER_INST_ID offerInstId, OBJ_TYPE ObjType, OBJ_ID objId, OFFER_OBJ_REL_ID offerObjRelId, ROLE_ID roleId,
    EFF_DATE effDate, EXP_DATE expDate, STATUS_CD statusCd, CREATE_STAFF createStaff, UPDATE_STAFF updateStaff, CREATE_DATE createDate, STATUS_DATE statusDate,
    UPDATE_DATE updateDate, LAST_ORDER_ITEM_ID lastOrderItemId, REMARK remark, ROUTE_ID routeId
FROM OFFER_OBJ_inst_REL
WHERE OBJ_ID = #{objId,jdbcType=BIGINT}
AND OFFER_INST_ID = #{offerInstId,jdbcType=BIGINT}
and route_id = #{routeId,jdbcType=BIGINT}
and EXP_DATE > #{expDate,jdbcType=VARCHAR}
and OFFER_OBJ_REL_ID = #{offerObjRelId,jdbcType=VARCHAR}

mapper.update:
    update offer_inst_attr


        EXP_DATE = #{expDate,jdbcType=DATE},


        STATUS_CD = #{statusCd,jdbcType=VARCHAR},


        STATUS_DATE = #{statusDate,jdbcType=DATE},


        remark = #{remark},


        UPDATE_DATE = #{updateDate,jdbcType=DATE}


    where OFFER_INST_ATTR_ID = #{offerInstAttrId,jdbcType=BIGINT}
	    and ROUTE_ID = #{routeId,jdbcType=BIGINT}

    update offer_obj_inst_rel


        EXP_DATE = #{expDate,jdbcType=DATE},


        STATUS_CD = #{statusCd,jdbcType=VARCHAR},


        STATUS_DATE = #{statusDate,jdbcType=DATE},


        UPDATE_DATE = #{updateDate,jdbcType=DATE},


        REMARK = #{remark,jdbcType=VARCHAR}


	   where OFFER_OBJ_INST_REL_ID = #{offerObjInstRelId,jdbcType=BIGINT}

		   and route_id = #{routeId,jdbcType=BIGINT}

update offer_inst


    EXP_DATE = #{expDate,jdbcType=DATE},


    STATUS_CD = #{statusCd,jdbcType=VARCHAR},


    STATUS_DATE = #{statusDate,jdbcType=DATE},


    remark = #{remark},


    UPDATE_DATE = #{updateDate,jdbcType=DATE}


 where  OFFER_INST_ID = #{offerInstId,jdbcType=BIGINT}
 and route_id  = #{routeId,jdbcType=BIGINT}

update offer_inst

  OWNER_CUST_ID=#{ownerCustId},
  STATUS_DATE=NOW()

 where  OFFER_INST_ID = #{offerInstId,jdbcType=BIGINT}
 and route_id  = #{routeId,jdbcType=BIGINT}

mapper.select:
    select OFFER_OBJ_INST_REL_ID  
     from OFFER_OBJ_INST_REL
     WHERE 1=1

		and OFFER_INST_ID = #{offerInstId}


		and OBJ_ID = #{objId}


		and OFFER_OBJ_REL_ID = #{offerObjRelId}


		and EXP_DATE = #{expDate,jdbcType=DATE}

	and route_id = #{routeId,jdbcType=BIGINT}  
	limit 1

    select OFFER_INST_ATTR_ID  
     from OFFER_INST_ATTR
     WHERE 1=1 

		and OFFER_INST_ID = #{offerInstId}


		and ATTR_ID = #{attrId}


		and ATTR_VALUE = #{attrValue}


		and EXP_DATE = #{expDate,jdbcType=DATE}

	and route_id = #{routeId,jdbcType=BIGINT}
	limit 1

  SELECT  OFFER_OBJ_REL_ID, OFFER_ID, OBJ_TYPE, OBJ_ID, ROLE_ID, EFF_DATE, EXP_DATE, APPLY_REGION_ID,
  STATUS_CD, CREATE_STAFF, UPDATE_STAFF, CREATE_DATE, STATUS_DATE, UPDATE_DATE, REMARK
FROM  OFFER_OBJ_REL
WHERE OFFER_ID=#{offerId}
 and ROLE_ID=#{roleId}

select OFFER_ID offerId,OFFER_NAME offerName
from offer
where OFFER_ID = #{offerId}

SELECT  OFFER_OBJ_INST_REL_ID offerObjInstRelId, OFFER_INST_ID offerInstId, OBJ_TYPE ObjType, OBJ_ID objId, OFFER_OBJ_REL_ID offerObjRelId, ROLE_ID roleId,
EFF_DATE effDate, EXP_DATE expDate, STATUS_CD statusCd, CREATE_STAFF createStaff, UPDATE_STAFF updateStaff, CREATE_DATE createDate, STATUS_DATE statusDate,
UPDATE_DATE updateDate, LAST_ORDER_ITEM_ID lastOrderItemId, REMARK remark, ROUTE_ID routeId
FROM OFFER_OBJ_inst_REL
WHERE OBJ_ID = #{objId,jdbcType=VARCHAR}
and offer_obj_rel_id = #{offerObjRelId,jdbcType=BIGINT}
and route_id = #{routeId,jdbcType=BIGINT}
and EXP_DATE > #{expDate,jdbcType=VARCHAR}
  and EFF_DATE >= #{effDate}
  and ((EFF_DATE <= #{expDate}
  and #{expDate} > #{effDate}
  and EXP_DATE > #{effDate})
  or
  (#{expDate} = #{effDate}
  and EFF_DATE <= #{effDate}
  and EXP_DATE > #{effDate}
  )
  or
  (#{expDate} < #{effDate}
  and EFF_DATE = #{effDate}
  and #{effDate} > now()
  ))

SELECT  offer_inst_id offerInstId,route_id routeId, OFFER_ID offerId, EFF_DATE effDate, EXP_DATE expDate
FROM OFFER_inst
WHERE OFFER_inst_id = #{offerInstId,jdbcType=BIGINT}
and route_id = #{routeId,jdbcType=BIGINT}
