mapper.select:
    select  SERVICE_OFFER_ID
    from ORD_BILL_OBJ
	where TABLE_NAME = 'ORD_OFFER_INST'
	      AND STATUS_CD = 'N'
          AND ARCH_GRP_ID = #{ARCH_GRP_ID}
          AND ORDER_ITEM_ID = #{ORDER_ITEM_ID}

    select  ATTR_VALUE attrValue
    from ORD_OFFER_INST_ATTR
	where ARCH_GRP_ID = #{ARCH_GRP_ID}
      AND OFFER_INST_ID = #{offerInstId}

    select  PROD_INST_ID prodInstId
    from ORD_OFFER_PROD_INST_REL
	where ARCH_GRP_ID = #{ARCH_GRP_ID}
      AND OFFER_INST_ID = #{offerInstId}

    select  OBJ_ID objId
    from ORD_OFFER_OBJ_INST_REL
	where ARCH_GRP_ID = #{ARCH_GRP_ID}
      AND OFFER_INST_ID = #{offerInstId}

mapper.update:
update ORD_OFFER_INST

    STATUS_CD = #{statusCd} ,
    REMARK = #{remark},
    UPDATE_DATE = #{updateDate}

where ARCH_GRP_ID = #{ARCH_GRP_ID}
and OFFER_INST_ID = #{offerInstId}

mapper.select:
SELECT ARCH_GRP_ID, ORDER_ITEM_ID, BILL_ACCT_ITEM_TYPE_ID billAcctItemTypeId, ACCT_ID acctId, PROD_INST_ID prodInstId, CREATE_DATE createDate, CHARGE_METHOD chargeMethod, STATUS_DATE statusDate, PAID_IN_AMOUNT paidInAmount, ORG_ID orgId, OFFER_INST_ID offerInstId, OFFER_ID offerId FROM ONE_ITEM_RESULT WHERE IF_CHARGE_OFF = 1
AND SUBSTR(LAN_ID, 1, 5) = #{regionId}
AND PROC_FLAG IN ('0', '2') AND CREATE_DATE > DATE_SUB(SYSDATE(),INTERVAL 30 day) and ARCH_GRP_ID = '310000234543' ORDER BY ARCH_GRP_ID, ORDER_ITEM_ID

mapper.update:
update ONE_ITEM_RESULT

    PROC_FLAG = #{statusCd},
    REMARKS = #{remarks},
    STATUS_DATE = #{statusDate}

where ARCH_GRP_ID = #{ARCH_GRP_ID}
and ORDER_ITEM_ID = #{ORDER_ITEM_ID}
