mapper.select:
select TASK_ID taskId,SERVER_IP serverIp,JOB_NAME jobName,TARGETOBJECT targetobject,TARGETMETHOD targetmethod, ARGUMENTS arguments,CRON_EXPRESSION cronExpression,IS_EFFECT isEffect,IS_START isStart,TASK_DESCRIBE taskDescribe, UPDATE_BY updateBy,UPDATE_DATE updateDate from tif_task_cfg where 1=1
AND SERVER_IP=#{SERVER_IP,jdbcType=VARCHAR}
and TASK_ID in (${TASK_ID})
AND TASK_ID=#{TASK_ID,jdbcType=VARCHAR}
